</code></pre>

C<pre><code>#define _GNU_SOURCE             /* See feature_test_macros(7) */
#include &lt;sched.h&gt;
#include &lt;malloc.h&gt; 
#include &lt;sys/types.h&gt; 
#include &lt;sys/wait.h&gt; 
#include &lt;signal.h&gt; 
#include &lt;sched.h&gt; 
#include &lt;stdio.h&gt; 
#include &lt;fcntl.h&gt; 
#include &lt;stdlib.h&gt;
#include &lt;stdint.h&gt;
#include &lt;stdlib.h&gt;


// 64kB stack 
#define STACK 1024*64 

// The child thread will execute this function 
int threadFunction( void* argument ) {
     printf( "child thread entering\n" ); 
     close((uintptr_t)argument); 
     printf( "child thread exiting\n" ); 
     return 0; 
}

int main() {
     void* stack; 
     pid_t pid; 
     int fd; 

     fd = open("/dev/null", O_RDWR); 
     if (fd &lt; 0) { 
         perror("/dev/null"); 
         exit(1); 
     } 

     // Allocate the stack 
     stack = malloc(STACK); 
     if (stack == 0) {
         perror("malloc: could not allocate stack"); 
         exit(1); 
     }
     printf("Creating child thread\n"); 

     // Call the clone system call to create the child thread 
     pid = clone(&amp;threadFunction, //
                 (char*) stack + STACK, //子进程栈在哪里
                 SIGCHLD | CLONE_FS | CLONE_FILES |
                  CLONE_SIGHAND | CLONE_VM, 
                 (void *)(uintptr_t)fd); 

     if (pid == -1) { 
          perror("clone"); 
          exit(2); 
     } 

     // Wait for the child thread to exit 
     pid = waitpid(pid, 0, 0); 
     if (pid == -1) { 
         perror("waitpid"); 
         exit(3); 
     } 

     // Attempt to write to file should fail, since our thread has 
     // closed the file. 
     if (write(fd, "c", 1) &lt; 0) { 
         printf("Parent:\t child closed our file descriptor\n"); 
     } 

     // Free the stack 
     free(stack); 

     return 0; 
}
</code></pre> <p>C<pre><code>#define _GNU_SOURCE
#include &lt;sched.h&gt;
#include &lt;sys/syscall.h&gt;
#include &lt;sys/wait.h&gt;
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;unistd.h&gt;

static int child_func(void* arg) {
  char* buf = (char*)arg;
  printf("[Child] Child sees buf = %p -  \"%s\"\n", buf, buf);
  strcpy(buf, "hello from child");
  return 0;
}

int main(int argc, char** argv) {
  // Allocate stack for child task.
  const int STACK_SIZE = 65536;
  char* stack = malloc(STACK_SIZE);
  if (!stack) {
    perror("malloc");
    exit(1);
  }

  // When called with the command-line argument "vm", set the CLONE_VM flag on.
  unsigned long flags = 0;
  if (argc &gt; 1 &amp;&amp; !strcmp(argv[1], "vm")) {
    flags |= CLONE_VM;
  }

  char buf[100];
  strcpy(buf, "hello from parent");
  if (clone(child_func, stack + STACK_SIZE, flags | SIGCHLD, buf) == -1) {
    perror("clone");
    exit(1);
  }

  int status;
  if (wait(&amp;status) == -1) {
    perror("wait");
    exit(1);
  }

  printf("[Parent] Child exited with status %d. buf = %p - \"%s\"\n", status,buf, buf);
  return 0;
}
</code></pre> - CLONE_VM：共享memory； - </p> <ul> <li>在fork情况下，父进程和子进程共享file descriptor（文件描述符 ），但不共享memory；</li> </ul>
